	ON TR.USER_ID = U.ID
LEFT JOIN zafira.JOBS J
	ON TR.JOB_ID = J.ID
LEFT JOIN zafira.TEST_SUITES TS
	ON TR.TEST_SUITE_ID = TS.ID




